import React,{useState,useEffect} from "react";
import {toast} from "react-toastify";
import{updateName,getName} from "./api";
import FormElement from "./form";
import Loading from "./loading";
import { useParams } from 'react-router-dom';
const Update=({history,match})=>{
    const[name,setName]=useState("");
    const[loading,setLoading]=useState(false);
    useEffect(()=>{
        loadName();
    },[]);
    const loadName=()=>{
        const {id}=useParams()
        getName(match.params.id).then((d)=>setName(d.data.name));
    };
const handleSubmit=(e)=>{
    e.preventDefault();
    setLoading(true);
    updateName(match.params.id,{name}).then((res)=>{
        setLoading(false);
        setName("");
        toast.success(`${res.data.name} is updated`);
        history.push("/");
    }).catch((err)=>{
        setLoading(false);
        if(err.response.status===400) toast.error(err.response.data);
    })
};
console.log("hello");
return(
    <div className="container-fluid">
        <div className="col-md-8">
            {loading ? <Loading/>:<h4>Update Name</h4>}
            <FormElement handleSubmit={handleSubmit} name={name} setName={setName}/>
        </div>
    </div>
);
};
export default Update;
