import React,{useState, useEffect } from "react";
import { toast } from "react-toastify";
import { getNames } from "./api";
import { EditOutlined, DeleteOutlined } from "@ant-design/icons";
import { Link } from "react-router-dom";
import FormElement from "./form";
import Loading from "./loading";
const task = () => {
    const [name, setName] = useState(" ");
    const [loading, setLoading] = useState(false);
    const [names, setNames] = useState([]);
    useEffect(() => {
        loadNames();
    }, []);
    const loadNames = () => getNames().then((name) => setNames(name.data));
    return (
        <div className="container-fluid">
            <div className="row">
                <div className="col-md-8">
                    {loading ? (<Loading />) :
                        (
                            <>
                                <h4>text</h4>
                                {names && names.map((temp) => (
                                    <div className="border row mx-2 align-items-center" key={temp.id}>
                                        <ul className="list-group">
                                            <li className="list-group-item">{temp.name}</li>
                                        </ul>
                                        <span className="btn btn-sm float-right">
                                            <DeleteOutlined className="text-danger" />
                                        </span>
                                        <Link to={`/update/${temp.id}`}>
                                            <span className="btn btn-sm float-right">
                                                <EditOutlined className="text-warning" />
                                            </span>
                                        </Link>
                                    </div>
                                ))
                                }
                            </>
                        )
                    }
                </div>
            </div>
        </div>
    )
};

export default task;
